mapper.select:
SELECT * FROM categories 
	WHERE member_id = #{memberId}
	ORDER BY "order"

SELECT COALESCE(MAX("order"), 0) AS maxOrder 
	FROM categories 
	WHERE member_id = #{memberId}

mapper.update:
UPDATE categories 
	SET "order" = "order" - 1 
	WHERE member_id = #{memberId} 
		AND "order" > #{order}

mapper.select:
SELECT id, contents, "order", color, createat, updateat, member_id
	FROM categories
	WHERE id = #{id}
