mapper.select:
    SELECT MAX(t_order)
    FROM todos
	    AND categories_id = #{categoriesId}

mapper.update:
UPDATE todos
SET t_order = t_order - 1
	WHERE t_order > #{order} 
	AND member_id = #{memberId} 
	AND categories_id = #{categoriesId}

mapper.select:
SELECT COALESCE(MAX(t_order), 0) AS maxOrder 
	FROM todos 
	WHERE categories_id = #{categoriesId}

mapper.update:
UPDATE todos 
	SET t_order = t_order - 1 
	WHERE categories_id = #{categoriesId} 
		AND t_order > #{order}
